import Vue from 'vue';
import VueRouter from 'vue-router';

// Importe os componentes que serão usados como páginas
import HomePage from '../views/HomePage.vue';
import BlocoAnotacoes from '../views/BlocoAnotacoes.vue';
import Notes from '../views/Notes.vue';
import EditNote from '../views/EditNote.vue';
import DetalhesNota from '../views/DetalhesNota.vue';

Vue.use(VueRouter);

// Configure as rotas
const routes = [
  { path: '/', redirect: '/HomePage' },  // Redireciona '/' para '/HomePage'
  { path: '/HomePage', component: HomePage },
  { path: '/BlocoAnotacoes', component: BlocoAnotacoes },
  { path: '/Notes', component: Notes },
  { path: '/editar-nota/:id', name: 'editar-nota', component: EditNote },
  { path: '/detalhes-nota/:id', name: 'detalhes-nota', component: DetalhesNota },
  // Adicione outras rotas conforme necessário
];

// Crie uma instância do VueRouter
const router = new VueRouter({
  routes
});

export default router;
