import Vue from 'vue';
import VueRouter from 'vue-router';

// Importe os componentes que serão usados como páginas
import HomePage from '../views/HomePage.vue';
import BlocoAnotacoes from '../views/BlocoAnotacoes.vue';
import Notes from '../views/Notes.vue';
import Sobre from '../views/Sobre.vue'; // Importe o componente Sobre

Vue.use(VueRouter);

// Configure as rotas
const routes = [
  { path: '/HomePage', component: HomePage },
  { path: '/BlocoAnotacoes', component: BlocoAnotacoes },
  { path: '/Notes', component: Notes },
  <v-list-item to="/Sobre" class="nav-link">Sobre</v-list-item>  // Adicione outras rotas conforme necessário
];

// Crie uma instância do VueRouter
const router = new VueRouter({
  routes
});

export default router;
